let slicer = 'Desconocido';
let printer = '';
let filament = '';        // Tipo: PLA, PETG, etc.
let filamentBrand = '';   // Marca: eSun, Prusament, etc.
let filamentUsed = '';    // Usado: 5.23m, 15g, etc.

// Lista de laminadores conocidos con variaciones de nombres
const slicers = {
    'Ultimaker Cura': ['cura', 'ultimaker cura', 'cura_steamengine', 'cura-steamengine'],
    'PrusaSlicer': ['prusaslicer', 'prusa slicer', 'prusaslicer'],
    'Bambu Studio': ['bambu studio', 'bambu_studio', 'bambu-studio'],
    'Creality Print': ['creality print', 'creality_print'],
    'Slic3r': ['slic3r', 'slic3r pe', 'slic3r_pe'],
    'IdeaMaker': ['ideamaker', 'idea_maker'],
    'Simplify3D': ['simplify3d', 'simplify 3d'],
    'MatterControl': ['mattercontrol', 'matter control'],
    'ReplicatorG': ['replicatorg', 'replicator g'],
    'FlashPrint': ['flashprint', 'flash print'],
    'ZeroSlicer': ['zeroslicer', 'zero slicer'],
    'Kiri Moto': ['kirimotors', 'kiri motors']
};

// Función para verificar si alguna palabra del laminador aparece en el G-code
function detectSlicer(gcode, slicerNames) {
    for (let slicerName of slicerNames) {
        let regex = new RegExp(slicerName, 'i');
        if (regex.test(gcode)) {
            return true;
        }
    }
    return false;
}

// Verificar slicer
for (let [name, variations] of Object.entries(slicers)) {
    if (detectSlicer(gcode, variations)) {
        slicer = name;
        break;
    }
}

// Detectar impresora con múltiples patrones comunes
let printerRegexes = [
    /; ?(MACHINE_NAME|printer_model) *= *(.+)/i,
    /; ?print_compatible_printers *= *(.+)/i,
    /; ?printer_name *= *(.+)/i,
    /; ?model *= *(.+)/i
];

for (let regex of printerRegexes) {
    let match = gcode.match(regex);
    if (match) {
        printer = match?.[2]?.trim() || '';
        break;
    }
}

// Detectar tipo de filamento (PLA, PETG, etc.)
let filamentTypeRegexes = [
    /; ?filament_type *= *(.+)/i,
    /; ?filament_material *= *(.+)/i
];

for (let regex of filamentTypeRegexes) {
    let match = gcode.match(regex);
    if (match) {
        filament = match?.[1]?.trim() || '';
        break;
    }
}

// Detectar marca del filamento
let filamentBrandRegexes = [
    /; ?filament_brand *= *(.+)/i,
    /; ?filament_settings_id *= *(.+)/i
];


for (let regex of filamentBrandRegexes) {
    let match = gcode.match(regex);
    if (match) {
        filamentBrand = match?.[1]?.trim() || '';
        break;
    }
}

// Detectar cantidad de filamento usado

// 1. Buscar exactamente: "; filament used [g] = ..."
let usedGramsExact = gcode.match(/; ?filament used \[g\] *= *([^\n]+)/i);
if (usedGramsExact) {
    filamentUsed = usedGramsExact[1].trim();
} else {
    // 2. Buscar una línea que contenga "filament_user" y también una "g"
    let userLineMatch = gcode.match(/^.*filament_user.*g.*$/gim);
    if (userLineMatch && userLineMatch.length > 0) {
        // Extraer solo el valor después del signo "=" si lo hay
        let valueMatch = userLineMatch[0].match(/= *([^\n]+)/);
        if (valueMatch) {
            filamentUsed = valueMatch[1].trim();
        } else {
            filamentUsed = userLineMatch[0].trim(); // Línea completa como fallback
        }
    } else {
        // 3. Buscar con los patrones comunes
        let filamentUsedRegexes = [
            /; ?filament used(?: *\[.*\])? *= *(.+)/i,
            /; ?filament_used *= *(.+)/i,
            /; ?filament_consumed *= *(.+)/i
        ];

        for (let regex of filamentUsedRegexes) {
            let match = gcode.match(regex);
            if (match) {
                filamentUsed = match[1].trim();
                break;
            }
        }
    }
}


// Resultado
msg.payload = {
    slicer: slicer,
    printer: printer,
    filament: filament,
    filament_brand: filamentBrand,
    filament_used: filamentUsed
};

return msg;

