// Generar timestamp
let timestamp = Date.now();
let maxletas = 5;

// Funci√≥n para generar 3 letras aleatorias
function generarLetrasAleatorias() {
    const letras = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let resultado = '';
    for (let i = 0; i < maxletas; i++) {
        resultado += letras.charAt(Math.floor(Math.random() * letras.length));
    }
    return resultado;
}

// Crear el ID
let randomLetras = generarLetrasAleatorias();
msg.payload = 'opr_' + timestamp + randomLetras;

return msg;

const data = msg.payload || {};
const printer = data.printer || {};
const job = data.job || {};
const id = data.id || {};
const displayLayerProgress = data.displayLayerProgress || {};
const gcode = data.gcode || {};
const img = data.img || {};
const printSuccessful = data.printSuccessful || {};
const roomTemperature = data.roomTemperature || {};

const files = data.files?.files || [];
const currentFilePath = job.job?.file?.path;

let matchedFile = files.find(f => f.path === currentFilePath);
let estimatedTime = matchedFile?.gcodeAnalysis?.estimatedPrintTime ?? (job.progress?.printTime ?? 0) + (job.progress?.printTimeLeft ?? 0);

msg.payload = {
    id: id,
    user_id: global.get('user_id'),
    type: "3DPrinter",
    timestamp: new Date().toISOString(),

    temperatures: {
        nozzle: {
            actual: {
                type: "Property",
                value: printer.temperature?.tool0?.actual ?? null,
                unitCode: "CEL"
            },
            target: {
                type: "Property",
                value: printer.temperature?.tool0?.target ?? null,
                unitCode: "CEL"
            }

        },
        bed: {
            actual: {
                type: "Property",
                value: printer.temperature?.bed?.actual ?? null,
                unitCode: "CEL"
            },
            target: {
                type: "Property",
                value: printer.temperature?.bed?.target ?? null,
                unitCode: "CEL"
            }
        }
    },

    environment: {
        roomTemperature: {
        value: roomTemperature ?? null,
        unitCode: "CEL"
        }
    },

    fanSpeed:{
        type: "Property",
        value: displayLayerProgress.fanSpeed ?? null,
        unitCode: "P1"
    },

    printTime: {
        elapsed: {
            type: "Property",
            value: job.progress?.printTime ?? null,
            unitCode: "SEC"
        },
        estimated: {
            type: "Property",
            value: estimatedTime,
            unitCode: "SEC"
        }
    },

    printer: {
        type: "Property",
        value: gcode.printer ?? null
    },

    filament: {
        type: "Property",
        value: {
            type: gcode.filament ?? null,
            brand: gcode.filament_brand ?? null,
            used: gcode.filament_used ?? null,
        }
    },

    slicer: {
        type: "Property",
        value: gcode.slicer ?? null
    },

    printSuccessful: {
        type: "Property",
        value: printSuccessful ?? null
    },

    status: {
        type: "Property",
        value: job.state ?? null
    },

    image: {
        type: "Property",
        value: img ?? null
    },

    file: {
        type: "Property",
        value: currentFilePath
    },
    progress:{
        type: "Property",
        value: isNaN(parseInt(displayLayerProgress.print.progress)) ? null : parseInt(displayLayerProgress.print.progress)
    },
    layer: {
        currentLayer: {
            type: "Property",
            value: displayLayerProgress.layer.current ?? null
        },
        totalLayers: {
            type: "Property",
            value: displayLayerProgress.layer.total ?? null,
        }
    }
};

return msg;

const nombreArchivo = msg.payload.job.file.path;
msg.filename = nombreArchivo;
msg.url = `${flow.get('OCTOPRINT_URL')}/downloads/files/local/${nombreArchivo}`;
msg.headers = {
    "X-Api-Key": flow.get('OCTOPRINT_API_KEY')
};
return msg;

let gcode = msg.payload;

let slicer = 'Desconocido';
let printer = '';
let filament = '';        // Tipo: PLA, PETG, etc.
let filamentBrand = '';   // Marca: eSun, Prusament, etc.
let filamentUsed = '';    // Usado: 5.23m, 15g, etc.

// Lista de laminadores conocidos con variaciones de nombres
const slicers = {
    'Ultimaker Cura': ['cura', 'ultimaker cura', 'cura_steamengine', 'cura-steamengine'],
    'PrusaSlicer': ['prusaslicer', 'prusa slicer', 'prusaslicer'],
    'Bambu Studio': ['bambu studio', 'bambu_studio', 'bambu-studio'],
    'Creality Print': ['creality print', 'creality_print'],
    'Slic3r': ['slic3r', 'slic3r pe', 'slic3r_pe'],
    'IdeaMaker': ['ideamaker', 'idea_maker'],
    'Simplify3D': ['simplify3d', 'simplify 3d'],
    'MatterControl': ['mattercontrol', 'matter control'],
    'ReplicatorG': ['replicatorg', 'replicator g'],
    'FlashPrint': ['flashprint', 'flash print'],
    'ZeroSlicer': ['zeroslicer', 'zero slicer'],
    'Kiri Moto': ['kirimotors', 'kiri motors']
};

// Funci√≥n para verificar si alguna palabra del laminador aparece en el G-code
function detectSlicer(gcode, slicerNames) {
    for (let slicerName of slicerNames) {
        let regex = new RegExp(slicerName, 'i');
        if (regex.test(gcode)) {
            return true;
        }
    }
    return false;
}

// Verificar slicer
for (let [name, variations] of Object.entries(slicers)) {
    if (detectSlicer(gcode, variations)) {
        slicer = name;
        break;
    }
}

// Detectar impresora con m√∫ltiples patrones comunes
let printerRegexes = [
    /; ?(MACHINE_NAME|printer_model) *= *(.+)/i,
    /; ?print_compatible_printers *= *(.+)/i,
    /; ?printer_name *= *(.+)/i,
    /; ?model *= *(.+)/i
];

for (let regex of printerRegexes) {
    let match = gcode.match(regex);
    if (match) {
        printer = match?.[2]?.trim() || '';
        break;
    }
}

// Detectar tipo de filamento (PLA, PETG, etc.)
let filamentTypeRegexes = [
    /; ?filament_type *= *(.+)/i,
    /; ?filament_material *= *(.+)/i
];

for (let regex of filamentTypeRegexes) {
    let match = gcode.match(regex);
    if (match) {
        filament = match?.[1]?.trim() || '';
        break;
    }
}

// Detectar marca del filamento
let filamentBrandRegexes = [
    /; ?filament_brand *= *(.+)/i,
    /; ?filament_settings_id *= *(.+)/i
];


for (let regex of filamentBrandRegexes) {
    let match = gcode.match(regex);
    if (match) {
        filamentBrand = match?.[1]?.trim() || '';
        break;
    }
}

// Detectar cantidad de filamento usado
let filamentUsedRegexes = [
    /; ?filament used(?: *\[.*\])? *= *(.+)/i,
    /; ?filament_used *= *(.+)/i,
    /; ?filament_consumed *= *(.+)/i
];


for (let regex of filamentUsedRegexes) {
    let match = gcode.match(regex);
    if (match) {
        filamentUsed = match?.[1]?.trim() || '';
        break;
    }
}

// Resultado
msg.payload = {
    slicer: slicer,
    printer: printer,
    filament: filament,
    filament_brand: filamentBrand,
    filament_used: filamentUsed
};

return msg;

var timerStart = flow.get('timerStart');

if (!timerStart) {
    flow.set('timerActive', false);
    return null;
}

var startTime = new Date(timerStart);
if (isNaN(startTime.getTime())) {
    flow.set('timerActive', false);
    return null;
}

var currentTime = new Date();
var diff = currentTime.getTime() - startTime.getTime(); // ‚Üê aqu√≠ la correcci√≥n
var thirtyMinutes = 30 * 60 * 1000;

if (diff < thirtyMinutes) {
    return msg;
} else {
    flow.set('timerActive', false);
    return null;
}

flow.set("timerActive", undefined);
flow.set("timerStart", undefined);  

msg.url = `${flow.get('OCTOPRINT_URL')}/api/job`;
msg.headers = {
    "X-Api-Key": flow.get('OCTOPRINT_API_KEY')
};
return msg;

function generateUUIDv4() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = Math.random() * 16 | 0,
            v = c === 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });
}

// Si hay ID v√°lido en el archivo
if (msg.payload && msg.payload.trim() !== "") {
    msg.id = msg.payload.trim();
    return [msg, null]; // Salida 1: ID le√≠do
} else {
    // Generar UUID y agregar timestamp
    const baseId = generateUUIDv4();
    const timestamp = Date.now(); // milisegundos desde √©poca UNIX
    const fullId = `uid_${baseId}-${timestamp}`;

    msg.id = fullId;
    msg.payload = fullId;
    return [null, msg]; // Salida 2: nuevo ID
}

global.set('user_id', msg.payload)
msg.payload = global.get('user_id')
return msg;
