// Función para generar 3 letras aleatorias
function generarLetrasAleatorias() {
    const letras = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let resultado = '';
    for (let i = 0; i < maxletas; i++) {
        resultado += letras.charAt(Math.floor(Math.random() * letras.length));
    }
    return resultado;
}

